import { gql } from "apollo-server-express";

const user = gql`
  type User {
    _id: ID
    firstName: String
    lastName: String
    email: String
    password: String
    gender: Gender
    receiver: [String]
    sender: [String]
    friendRequests: [User]
    friends: [User]
    groups: [Group]
    # hobby: [String!]
    # profile: String
    # status: String
    # friends: [ID]
    # blockedUsers: [ID]
    createdAt: String
    updatedAt: String
  }

  enum Gender {
    male
    female
  }

  type userResult {
    token: String
  }

  input createUserInput {
    firstName: String
    lastName: String
    email: String
    password: String
    gender: Gender
    # receiver:
    # hobby: [String!]
    # profile: String
  }

  type Response {
    message: String
  }

  input loginUserInput {
    email: String
    password: String
  }

  type Query {
    getUser: [User]
    GetMe: User
    GetMyFriends: User
    suggestionOfUser: [User]
    searchFriend(searchText: String): [User]
  }

  type Mutation {
    createUser(input: createUserInput): User
    loginUser(input: loginUserInput): userResult
    sendFriendRequest(friendId: String!): Response
    acceptFriendRequest(friendId: String!): Response
    deleteFriend(friendId: String!): Response
  }
`;

export default user;

