      //             $expr: {
      //               $or: [
      //                 { $eq: ["$admin", "$$userId"] },
      //                 { $in: ["$$userId", "$members"] },
      //               ],
      //             },
      //           },
      //         },
      //         {
      //           $project: {
      //             _id: 1,
      //             groupName: 1,
      //           },
      //         },
